import { useState } from "react";
import Table from "react-bootstrap/Table";
import { Button, Form } from "react-bootstrap";
import { deleteTrans } from "../../utils/axiosHelper";


export const TransTable = ({ trans=[] }) => {
  const [itemToDelete, setItemToDelete] = useState([]);
  const [isAllSelected, setIsAllSelected] =useState(false);

  const handleOnSelect =(e) => {
    const {checked, value}=e.target;


    if(checked){
      setItemToDelete([...itemToDelete, value])
      setIsAllSelected(trans.length === itemToDelete.length + 1)
    }else{
      const tempArg = itemToDelete.filter((item)=>item!==value);
      setItemToDelete(tempArg);
      setIsAllSelected(false)
    }
    console.log(checked,value);
  }

  const handleOnSelectAll = (e) => {
    const {checked,value} = e.target;

    if(checked ){
      setItemToDelete(trans.map((item)=>item._id))
      setIsAllSelected(true)
    }else{
      setItemToDelete([]);
      setIsAllSelected(false)
    }
  }
  console.log(itemToDelete);

  const total =  trans.reduce(
    (acc, { type, amount }) =>
      type === "income" ? acc + amount : acc - amount,
    0
  );

  const handleOnDelete = async () => {
    if (
      window.confirm(
        `Are you sure you want to delete ${itemToDelete.length}  transactions?`
      )
    ) {
      const { status, message } = await deleteTrans(itemToDelete);
      if (status === "success") {
        setItemToDelete([]);
        getTrans();
      }
    }
  };
  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>
              <Form.Check 
              type="checkbox"
              onChange={handleOnSelectAll}
              checked={trans.length === itemToDelete.length}/>

            </th>
            <th>Date</th>
            <th>Transaction</th>
            <th>Income</th>
            <th>Expenses</th>
          </tr>
        </thead>
        <tbody>
          {trans.map((item, i) => (
            <tr key={item._id}>
              <td>
              <Form.Check 
                type="checkbox" 
                onChange={handleOnSelect}
                value={item._id}
                checked={itemToDelete.includes(item._id)}
              />
              </td>
              <td>{new Date(item.createdAt).toLocaleTimeString()}</td>

              <td>{item.name}</td>
              {item.type === "income" ? (
                <>
                  <td>$ {item.amount}</td>
                  <td></td>
                </>
              ) : (
                <>
                  <td></td>
                  <td className="text-danger">-$ {item.amount}</td>
                </>
              )}
            </tr>
          ))}

          <tr className="fw-bolder">
            <td colSpan={3}>Total balance</td>
            <td>${total}</td>
          </tr>
        </tbody>
      </Table>
      {
        itemToDelete.length ? (
          <div className="d-grid">
        <Button varient="danger">Delete 
        {itemToDelete.length} item(s)
        </Button>
        </div>
      )
      : null
      }
    </>
  );
};
