import { useState } from "react";
import Table from "react-bootstrap/Table";
import { Form } from "react-bootstrap";

export const TransTable = ({ trans=[] }) => {
  const [itemToDelete, setItemToDelete] = useState([]);
  const [itemToDeleteAll, setItemToDeleteAll] =useState(false);

  const handleOnSelect =(e) => {
    const {checked, value}=e.target;

    if(checked){
      setItemToDelete([...itemToDelete, value])
    }else{
      const tempArg = itemToDelete.filter((item)=>item!==value);
      setItemToDelete(tempArg);
    }
    console.log(checked,value);
  }

  const handleOnSelectAll = (e) => {
    const {checked,value} = e.target;

    if (checked) {
      setItemToDelete([...itemToDelete, trans.map((item)=>item._id) ]);
    }

    else {
      setItemToDelete([]);
    }
  }
  console.log(itemToDelete);

  const total =  trans.reduce(
    (acc, { type, amount }) =>
      type === "income" ? acc + amount : acc - amount,
    0
  );
  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>
            <Form.Check 
            type="checkbox"
            onChange={handleOnSelectAll}
            checked={trans.length === itemToDelete.length}/>

          </th>
          <th>Date</th>
          <th>Transaction</th>
          <th>Income</th>
          <th>Expenses</th>
        </tr>
      </thead>
      <tbody>
        {trans.map((item, i) => (
          <tr key={item._id}>
            <td>
            <Form.Check 
              type="checkbox" 
              onChange={handleOnSelect}
              value={item._id}
              checked={itemToDelete.includes(item._id)}
            />
            </td>
            <td>{new Date(item.createdAt).toLocaleTimeString()}</td>

            <td>{item.name}</td>
            {item.type === "income" ? (
              <>
                <td>$ {item.amount}</td>
                <td></td>
              </>
            ) : (
              <>
                <td></td>
                <td className="text-danger">-$ {item.amount}</td>
              </>
            )}
          </tr>
        ))}

        <tr className="fw-bolder">
          <td colSpan={3}>Total balance</td>
          <td>${total}</td>
        </tr>
      </tbody>
    </Table>
  );
};
